package pl.coderslab.charity.converters;import org.springframework.stereotype.Component;import pl.coderslab.charity.entities.User;import pl.coderslab.charity.models.RegistrationForm;@Componentpublic class UserConverter {    public User convertRegistrationToUser(RegistrationForm registration) {        User user = new User();        user.setFirstName(registration.getFirstName());        user.setLastName(registration.getLastName());        user.setEmail(registration.getEmail());        user.setPassword(registration.getPass1());        return user;    }}