package pl.coderslab.charity.entities;import lombok.Getter;import lombok.Setter;import javax.persistence.*;import javax.validation.constraints.Positive;import javax.validation.constraints.PositiveOrZero;import java.time.LocalDate;import java.time.LocalTime;import java.util.List;@Entity@Getter@Setter@Table(name = "donations")public class Donation {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Positive    private Integer quantity;    @OneToMany(targetEntity = Category.class, mappedBy = "donation")    private List<Category> categories;    @ManyToOne(targetEntity = Institution.class)    private Institution institution;    private String street;    private String city;    private String zipCode;    private LocalDate pickUpDate;    private LocalTime pickUpTime;    private String pickUpComment;    public Donation() {    }}