package pl.coderslab.charity.services;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Service;import pl.coderslab.charity.repositories.DonationRepository;@Service@Slf4j@RequiredArgsConstructorpublic class DonationService {    private final DonationRepository donationRepository;    public Long countQuantityOfDonatedBags() {        if (donationRepository.countAllQuantities()==null){            return 0L;        }else return donationRepository.countAllQuantities();    }    public long countDonations() {        return donationRepository.count();    }}