package pl.coderslab.charity.services;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;import pl.coderslab.charity.entities.User;import pl.coderslab.charity.repositories.UserRepository;import java.util.List;@Service@RequiredArgsConstructorpublic class UserService {    private final UserRepository userRepository;    public List<User> showUsers() {        return userRepository.findAll();    }    public void addUser(User user) {        userRepository.save(user);    }}